        finishedStatusSetter(false);
        quoteElement.innerText = '';
        getNext();
    }

    function timer() {
        console.log("Timer started...");
        var startTime = new Date();
        setInterval(() => {
            if(finishedStatus){
                return;
            }
            timeSetter((new Date() - startTime) / 1000);
        }, 1000);
        console.log("Timer ended...");
    }

