import React from 'react';

export default (state, action) => {
    switch(action.type) {
        case 'LOG_IN': 
            return {
                loggedIn: [action.payload, ...state.loggedIn]
            }
        case 'LOG_OUT':
            return {
                loggedIn: [action.payload]
            }
        default:
            return state;
    }
}
