import { keyboard } from '@testing-library/user-event/dist/keyboard';
import { getNextKeyDef } from '@testing-library/user-event/dist/keyboard/getNextKeyDef';
import React from 'react';


const Body = () => {
    console.log("Loaded body...");
    
    const [started, startedSetter] = React.useState(false);
    const [globalQuote, globalQuoteSetter] = React.useState('');
    const [wpm, wpmSetter] = React.useState(0);
    const [wordCount, wordCountSetter] = React.useState(0);
    const [time, timeSetter] = React.useState(0);
    const [errorCount, errorCountSetter] = React.useState(0);
    const [finishedStatus, finishedStatusSetter] = React.useState(false);

    const quoteElement = useRef(null);
    const inputElement = useRef(null);
    
    function changeStarted() {
        startedSetter(true);
        getNext();
    }

    
    function startProcess() {
        console.log('startProcess: started...');
        globalQuoteSetter('');
        wpmSetter(0);
        wordCountSetter(0);
        timeSetter(0);
        errorCountSetter(0);
        finishedStatusSetter(false);
        quoteElement.current.innerText = '';
        getNext();
    }

    function timer() {
        console.log("Timer started...");
        var startTime = new Date();
        setInterval(() => {
            if(finishedStatus){
                return;
            }
            timeSetter((new Date() - startTime) / 1000);
        }, 1000);
        console.log("Timer ended...");
    }

    function wordCounter(thisQuote) {
        const arr = thisQuote.split(' ');
        wordCountSetter(arr.length);        
    }
    
    function getQuote() {
        return fetch('https://api.quotable.io/random')
            .then(response => response.json())
            .then(data => data.content)
    }

    async function getNext() {
        const quote = await getQuote();
        globalQuoteSetter(quote);
        wordCounter(quote);
        quote.split('').forEach(character => {
            const characterS = document.createElement('span');
            characterS.innerText = character;
            quoteElement.current.appendChild(characterS);
        })
        inputElement.current.value = null
    }
    
    function keystrokes() {
        if(time == 0){
            finishedStatusSetter(false);
            timer();
        }
        const arrayQuote = document.querySelectorAll('span');
        const arrayValue = inputElement.value.split('');
        let correct = true;
        arrayQuote.forEach((characterS, index) => {
            const character = arrayValue[index]
            if(character == null) {
                characterS.classList.remove('correct')
                characterS.classList.remove('incorrect')
                correct = false;
            }
            else if (character === characterS.innerText) {
                characterS.classList.add('correct');
                characterS.classList.remove('incorrect')
            }
            else{
                characterS.classList.remove('correct')
                characterS.classList.add('incorrect')
                correct = false;
            }
        })
        if(correct){
            finishedStatusSetter(true);
        }
    }
    
    if(!started){
        return (
        <div className="m-10 my-20 flex">
            <div onClick={changeStarted} className="text-white absolute left-[500px] hover:cursor-pointer mt-20 p-2 px-4 text-[2rem] shadow-lg bg-[#00233b] rounded-lg text-center">
                CLICK TO BEGIN
            </div>
            <div className="rounded-lg bg-[#00233b] h-1/3 w-1/5 absolute right-10 ml-10">
                <div className="flex p-5 flex-col">
                    <div className="text-[#F4D47E] text-5xl text-center my-2">
                        {wpm} WPM
                    </div>
                    <div className="text-[#F4D47E] text-5xl text-center my-2">
                        {time} s.
                    </div>
                </div>
            </div>
        </div>
        )
    }
    else{
    return (
        <div className="m-10 my-20 flex">
            <div ref={quoteElement} id="quote" className="text-[4rem] text-[#777777] break-words max-w-[75%]">
                Sample Text here...aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa
            </div>
            <textarea ref={inputElement} onChange={keystrokes} id="input" className="text-[1px] text-white bg-[#012946]" defaultValue="chickenman">
            </textarea>
            <div className="rounded-lg bg-[#00233b] h-1/3 w-1/5 absolute right-10 ml-10">
                <div className="flex p-5 flex-col">
                    <div className="text-[#F4D47E] text-5xl text-center my-2">
                        {wpm} WPM
                    </div>
                    <div className="text-[#F4D47E] text-5xl text-center my-2">
                        {time} s.
                    </div>
                </div>
            </div>
        </div>
        
    )
    }
}

export default Body;
