import * as api from '../api/index.js';
import Auth from '../pages/Auth.js';

export const signin = (formData, nav) => async (dispatch) => {
    try {
        const { data } = await api.signInUser(formData.username, formData.password);
        dispatch({ type: 'Auth', data })
        nav('/');
    } catch (error) {
        console.log(error);
    }
};

export const signup = (formData, nav) => async (dispatch) => {
    try {
        const { data } = await api.signInUser(formData.username, formData.password);
        dispatch({ type: 'Auth', data })
        nav('/');
    } catch (error) {
        console.log(error);
    }
};

export const updateWpm = (formData, nav) => async (dispatch) => {
    try {
        const { data } = await api.updateUser(formData.username, formData.wpm, formData.raceCount);
        dispatch({ type: 'Update', formData });
        nav('/');
    } catch (error) {
        console.log(error);
    }
}
