import React from 'react';

const Body = () => {
    var globalQuote;
    const [wordCount, countSetter] = React.useState(0);
    const [wpm, wpmSetter] = React.useState(0);
    const [time, timeSetter] = React.useState(0);
    const quoteElement = document.getElementById('quote');
    const inputElement = document.getElementById('input');

    function wordCounter(thisQuote) {
        const arr = thisQuote.split(' ');
        countSetter(arr.length);        
    }

    function getQuote() {
        return fetch('https://api.quotable.io/random')
            .then(response => response.json())
            .then(data => data.content)
    }

    async function getNext() {
        const quote = await getQuote();
        globalQuote = quote;
        quoteElement.innerText = '';
        wordCounter(quote);
        quote.split('').forEach(character => {
            const characterS = document.createElement('span')
            characterS.innerText = character;
            quoteElement.appendChild(characterS);
        })
        
        inputElement.value = null
    }



    return (
        <div className="m-10 my-20">
            <div id="quote" className="text-[4rem] text-[#777777]">
                Sample Text here...
            </div>
            <div id="input" className="text-[1px] text-white">
                hi
            </div>
            <div className="rounded-lg bg-[#00233b] h-1/3 w-1/5 absolute right-10 ml-10">
                <div className="flex p-5 flex-col place-content-around">
                    <div className="text-[#F4D47E] text-5xl text-center">
                        {wpm} WPM
                    </div>
                    <div className="text-[#F4D47E] text-5xl text-center">
                        {time} s.
                    </div>
                </div>
            </div>
        </div>
        
    )
}

export default Body;
