import React from 'react';


const Body = () => {
    //STARTS HERE
    const [started, startedSetter] = React.useState(false);
    const [wpm, wpmSetter] = React.useState(0);
    const [wordCount, wordCountSetter] = React.useState(0);
    const [finishedStatus, finishedStatusSetter] = React.useState(false);
    const [globalQuote, globalQuoteSetter] = React.useState('');
    const [intervalVar, intervalVarSetter] = React.useState();

    const quoteElement = React.useRef(null);
    const inputElement = React.useRef(null);
    const timeRef = React.useRef(null);
    const wpmRef = React.useRef(null);
    const errorRef = React.useRef(null);

    const [time, timeSetter] = React.useState(0);

    async function changeStarted() {
        console.log("changeStarted()");
        startedSetter(true);
        globalQuoteSetter(getQuote());
        wpmSetter(0);
        wordCountSetter(0);
        timeSetter(0);
        finishedStatusSetter(false);
    }

    React.useEffect(() => {
        if(started === true){
            console.log("started was true");
            quoteElement.current.value = null;
            inputElement.current.value = null;
            getNext();
        }
        else{
            console.log("started was false");
        }
    },[started]);

    

    function timer() {
        console.log("timer()");
        timeSetter(-1);
        var startTime = new Date();
        intervalVarSetter(setInterval(() => {
            timeRef.current.innerText = Math.floor((new Date() - startTime) / 1000);
            wpmRef.current.innerText = Math.floor(wordCount / (Math.floor((new Date() - startTime) / 1000) / 60));
        }, 1000));
    }

    function wordCounter(thisQuote) {
        const arr = thisQuote.split(' ');
        wordCountSetter(arr.length);        
    }
    
    function getQuote() {
        return "chicken bobby brown moonkey chicknen";
        return fetch('https://api.quotable.io/random')
            .then(response => response.json())
            .then(data => data.content)
    }

    async function getNext() {
        console.log("getNext()");
        wordCounter(globalQuote);
        globalQuote.split('').forEach(character => {
            const characterS = document.createElement('span');
            characterS.innerText = character;
            quoteElement.current.appendChild(characterS);
        })
    }
    
    function keystrokes() {
        if(time === 0){
            finishedStatusSetter(false);
            timer();
        }
        const arrayQuote = quoteElement.current.querySelectorAll('span');
        const arrayValue = inputElement.current.value.split('');
        let correct = true;
        arrayQuote.forEach((characterS, index) => {
            const character = arrayValue[index]
            if(character == null) {
                characterS.classList.remove('correct')
                characterS.classList.remove('incorrect')
                correct = false;
            }
            else if (character === characterS.innerText) {
                characterS.classList.add('correct');
                characterS.classList.remove('incorrect')
            }
            else{
                characterS.classList.remove('correct')
                characterS.classList.add('incorrect')
                errorRef.current.innerText = parseInt(errorRef.current.innerText) + 1;
                correct = false;
            }
        })
        if(correct){
            finishedStatusSetter(true);
            clearInterval(intervalVar);
            wpmSetter(parseInt(wpmRef.current.innerText));
            console.log("finished");
        }
    }
    
    if(!started || finishedStatus){
        return (
        <div className="m-10 my-20 flex">
            <div onClick={changeStarted} className="text-white absolute left-[500px] hover:cursor-pointer mt-20 p-2 px-4 text-[2rem] shadow-lg bg-[#00233b] rounded-lg text-center">
                CLICK TO BEGIN
            </div>
            <div className="rounded-lg bg-[#00233b] h-1/3 w-1/5 absolute right-10 ml-10">
                <div className="flex p-5 flex-col">
                    <div className="text-[#F4D47E] text-5xl text-center my-2">
                    <span ref={wpmRef}>0</span> WPM
                    </div>
                    <div className="text-[#F4D47E] text-5xl text-center my-2">
                    <span ref={timeRef}>0</span> s.
                    </div>
                </div>
            </div>
        </div>
        )
    }
    else{
    return (
        <div className="grid grid-rows-[60vh_20vh]">
            <div ref={quoteElement} id="quote" className="p-10 text-[4rem] text-[#777777] break-words text-center">
            </div>
            <textarea ref={inputElement} onChange={keystrokes} id="input" className="text-[1px] text-white bg-[#012946]" defaultValue="">
            </textarea>
            <div className="rounded-lg bg-[#00233b] w-1/2 h-[15vh] mx-auto row-start-2 row-end-3">
                <div className="flex place-content-evenly pt-3">
                    <div className="text-[#F4D47E] text-5xl text-center flex flex-col">
                        WPM
                        <span ref={wpmRef}>0</span>
                    </div>
                    <div className="text-[#F4D47E] text-5xl text-center flex flex-col">
                        ERRORS
                        <span ref={errorRef}>0</span> 
                    </div>
                </div>
            </div>
        </div>
        
    )
    }
}

export default Body;
