import axios from 'axios';

const url = 'http://localhost:5000/users';

export const getUser = (username) => axios.put(url, { username: username });

export const signInUser = (username, password) => axios.put(url + "/signin", { username, password, wpm: 0, raceCount: 0 });

export const signUpUser = (username, password) => axios.put(url + "/signup", { username, password, wpm: 0, raceCount: 0 });

export const updateUser = (username, wpm, newRC) => axios.put(url + "/update", { username, wpm, raceCount: newRC });
