import React from 'react';


const Body = () => {
    console.log("Loaded body...");

    const [globalQuote, globalQuoteSetter] = React.useState('');
    const [wpm, wpmSetter] = React.useState(0);
    const [wordCount, wordCountSetter] = React.useState(0);
    const [time, timeSetter] = React.useState(0);
    const [errorCount, errorCountSetter] = React.useState(0);
    const [finishedStatus, finishedStatusSetter] = React.useState(false);
    
    const quoteElement = document.getElementById('quote');
    const inputElement = document.getElementById('input');

    function startProcess() {
        wpmSetter(0);
        wordCountSetter(0);
        timeSetter(0);
        errorCountSetter(0);
        finishedStatusSetter(0);
        globalQuoteSetter('');
    }

    function timer() {
        console.log("Timer started...");
        timeSetter(0);
        var startTime = new Date();
        setInterval(() => {
            if(finishedStatus){
                return;
            }
            timeSetter((new Date() - startTime) / 1000);
        }, 1000);
        console.log("Timer ended...");
    }

    function wordCounter(thisQuote) {
        const arr = thisQuote.split(' ');
        wordCountSetter(arr.length);        
    }
    /*
    function getQuote() {
        return fetch('https://api.quotable.io/random')
            .then(response => response.json())
            .then(data => data.content)
    }

    async function getNext() {
        const quote = await getQuote();
        globalQuoteSetter(quote);
        quoteElement.innerText = '';
        wordCounter(quote);
        quote.split('').forEach(character => {
            const characterS = document.createElement('span')
            characterS.innerText = character;
            quoteElement.appendChild(characterS);
        })
        
        inputElement.value = null
    }

    inputElement.addEventListener('input', () => {
        if(time == 0){
            statusSetter(false);
            time();
        }
        const arrayQuote = quoteElement.querySelectorAll('span');
        const arrayValue = inputElement.value.split('');
        let correct = true;
        arrayQuote.forEach((characterS, index) => {
            const character = arrayValue[index]
            if(character == null) {
                characterS.classList.remove('correct')
                characterS.classList.remove('incorrect')
                correct = false;
            }
            else if (character === characterS.innerText) {
                characterS.classList.add('correct');
                characterS.classList.remove('incorrect')
            }
            else{
                characterS.classList.remove('correct')
                characterS.classList.add('incorrect')
                correct = false;
            }
        })
        if(correct){
            statusSetter(true);
        }
    })
    */

    return (
        <div className="m-10 my-20 flex">
            <div id="quote" className="text-[4rem] text-[#777777] break-words max-w-[75%]">
                Sample Text here...aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa
            </div>
            <div id="input" className="text-[1px] text-white">
                hi
            </div>
            <div className="rounded-lg bg-[#00233b] h-1/3 w-1/5 absolute right-10 ml-10">
                <div className="flex p-5 flex-col">
                    <div className="text-[#F4D47E] text-5xl text-center my-2">
                        {wpm} WPM
                    </div>
                    <div className="text-[#F4D47E] text-5xl text-center my-2">
                        {time} s.
                    </div>
                </div>
            </div>
        </div>
        
    )
}

export default Body;
